import '../styles/App.css';
import menu from '../images/lista.png';
import arrow from '../images/arrow.png';
import { useState } from 'react';

function App() {
  const [isOpen, setIsOpen] = useState('hidden');

  const handleMenuClick = (ev) => {
    ev.preventDefault();
    setIsOpen(true);
  };

  const handleCloseClick = (ev) => {
    ev.preventDefault();
    setIsOpen(false);
  };

  return (
    <div className="App">
      <header>
        <div>
          <img src={menu} className="menu" alt="menu hamburguesa" onClick={handleMenuClick}></img>
        </div>
        <div>
          <img src={arrow} className="menu" alt="" onClick={handleCloseClick}></img>
        </div>
      </header>
      <nav>
        <ul>
          <li>
            <a href="#">Home</a>
          </li>
          <li>
            <a href="#">Servicios</a>
          </li>
          <li>
            <a href="#">Blog</a>
          </li>
          <li>
            <a href="#">Contacto</a>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default App;
