import '../styles/App.css';
import menu from '../images/lista.png';

import { useState } from 'react';

function App() {
  const [isOpen, setIsOpen] = useState('hidden');

  const handleMenuClick = (ev) => {
    ev.preventDefault();
  };

  return (
    <div className="App">
      <header>
        <img src={menu} className="menu" alt="menu hamburguesa" onClick={handleMenuClick}></img>
      </header>
      <div className={isOpen}>
        <span onClick={handleMenuClick}>Ocultar menú</span>
        <ul>
          <li>
            <a href="#">Home</a>
          </li>
          <li>
            <a href="#">Servicios</a>
          </li>
          <li>
            <a href="#">Blog</a>
          </li>
          <li>
            <a href="#">Contacto</a>
          </li>
        </ul>
      </div>
    </div>
  );
}

export default App;
